mapper.select:
SELECT COUNT(*)
FROM ASSOCIATION
WHERE MEMBER_ID = #{memberId}

mapper.update:
		UPDATE COMPANY
		SET COMPANY_NAME = #{companyName},
    	COMPANY_ADDR = #{companyAddr},
    	COMPANY_TEL = #{companyTel}
	WHERE COMPANY_ID = #{companyId}

mapper.delete:
UPDATE COMPANY
SET COMPANY_REMOVE_WHETHER  = 'Y'
WHERE COMPANY_ID = #{companyId}
