mapper.select:
SELECT * FROM ( SELECT a.*, ROWNUM rnum FROM ( SELECT PI.PROJECT_ISSUE_ID,PI.ISSUE_TITLE, PI.ISSUE_CONTENT, PI.ISSUE_EVENT_DATE , PI.PROCESS_ET_DATE ,PI.PROJECT_STATUS_ID , PS.PROJECT_STATUS_NAME , PIC.PROJECT_ISSUE_CODE_NAME, PI.PROJECT_ISSUE_WRITER , PI.PROJECT_ISSUE_COMMENT, PI.PROJECT_ROLE_ID ,(SELECT TRACKER_NAME FROM TRACKER WHERE PI.TRACKER_ID = TRACKER.TRACKER_ID) TRACKER_NAME ,(select PA.PROJECT_ROLE_ID from MEMBER M JOIN PROJECT_ATTEND PA on M.MEMBER_ID = PA.MEMBER_ID WHERE M.MEMBER_ID = #{memberId} AND PROJECT_ID =#{projectId}) AS MY_ROLE_ID , M.MEMBER_IMAGE_FILE_ROUTE , M.FILE_EXTENSION FROM PROJECT_ISSUE PI JOIN PROJECT_STATUS PS on PI.PROJECT_STATUS_ID = PS.PROJECT_STATUS_ID JOIN PROJECT_ISSUE_CODE PIC on PI.PROJECT_ISSUE_CODE_ID = PIC.PROJECT_ISSUE_CODE_ID LEFT JOIN MEMBER M ON M.MEMBER_NAME = PROJECT_ISSUE_WRITER WHERE PROJECT_ID = #{projectId}
and PI.ISSUE_TITLE like '%' || #{search} || '%'
ORDER BY PI.PROJECT_STATUS_ID ) a WHERE ROWNUM <= #{currentPage} * #{pageSize} ) WHERE rnum > (#{currentPage} - 1) * #{pageSize}

mapper.insert:
SELECT NVL(MAX(PROJECT_ISSUE_ID),0)
		FROM PROJECT_ISSUE

INSERT INTO PROJECT_ISSUE (
PROJECT_ID , MEMBER_ID , COMPANY_ID , PROJECT_ROLE_ID
, PROJECT_ISSUE_CODE_ID
, TRACKER_ID
, ISSUE_TITLE
, ISSUE_CONTENT
, PROJECT_ISSUE_WRITER
, PROCESS_ET_DATE
, PROJECT_ISSUE_PIC_ID
, PROJECT_ISSUE_PIC_NAME
)
VALUES (#{projectId}, #{memberId}, #{companyId}, #{projectRoleId}
, #{projectIssueCodeId}
, #{trackerId}
, #{issueTitle}
, #{issueContent}
, #{projectIssueWriter}
, #{processEtDate}
, #{projectIssuePicId}
, #{projectIssuePicName}
)

mapper.select:
select PROJECT_ROLE_ID
from PROJECT_ATTEND
where MEMBER_ID = #{memberId}
  and PROJECT_ID = #{projectId}

mapper.update:
UPDATE PROJECT_ISSUE
SET
    PROJECT_ISSUE_COMMENT = #{projectIssueComment}

WHERE PROJECT_ISSUE_ID = #{projectIssueId}
