mapper.update:
UPDATE MEMBER
   SET
	   MEMBER_GRADE_ID = 2 WHERE MEMBER_ID = #{memberId}

		UPDATE MEMBER
		SET MEMBER_EMAIL = #{memberEmail}
		,MEMBER_TEL =#{memberTel}


			 , MEMBER_IMAGE_FILE_ROUTE = #{memberImageFileRoute}
   			, ORIGINAL_FILE_NAME = #{originalFileName}
  			 , SAVE_FILE_NAME = #{saveFileName}
  			, FILE_EXTENSION = #{fileExtension}

		WHERE MEMBER_ID = #{memberId}

mapper.select:
SELECT COUNT(*)
FROM MEMBER
WHERE MEMBER_TEL = #{memberTel}

mapper.delete:
UPDATE MEMBER
SET MEMBER_SECESSION_WHETHER = 'Y'
WHERE MEMBER_ID = #{memberId}

mapper.select:
	SELECT *
		FROM (
	SELECT a.*, ROWNUM as rnum
	FROM (
		SELECT *
		FROM MEMBER M JOIN ASSOCIATION A2 on M.MEMBER_ID = A2.MEMBER_ID
		JOIN COMPANY C ON A2.COMPANY_ID = C.COMPANY_ID
		 WHERE  M.MEMBER_GRADE_ID != 1

			AND C.COMPANY_ID = #{companyId}

		ORDER BY M.MEMBER_ID
	) a
) WHERE rnum BETWEEN (#{currentPage} - 1) * #{pageSize} + 1 AND #{currentPage} * #{pageSize}
