mapper.select:
SELECT ORG.*, LEVEL, A.MEMBER_ID, M.MEMBER_NAME
FROM ORGCHART ORG JOIN ASSOCIATION A ON ORG.ORGCHART_ID = A.ORGCHART_ID JOIN MEMBER M ON A.MEMBER_ID = M.MEMBER_ID WHERE ORG.ORGCHART_ID IN ( SELECT ORGCHART_ID FROM ORGCHART START WITH COMPANY_ID = #{companyId} AND ORGCHART_ID = #{orgchartId} CONNECT BY PRIOR ORGCHART_ID = SENIOR_DEPARTMENT_ID ) START WITH ORG.COMPANY_ID = #{companyId} AND ORG.ORGCHART_ID = #{orgchartId}
CONNECT BY PRIOR ORG.ORGCHART_ID = ORG.SENIOR_DEPARTMENT_ID ORDER BY LEVEL

SELECT MEMBER_NAME
FROM MEMBER
WHERE MEMBER_ID = #{memberId}

mapper.delete:
DELETE FROM CHATTING_LIST

WHERE
    CHATTING_ID = #{chattingId}
    AND MEMBER_ID = #{memberId}
